CREATE TABLE ESPECTACULO
(
	ID NUMBER NOT NULL,
	ID_COMPANIA NUMBER NOT NULL,
	NOMBRE VARCHAR(255),
	DURACION NUMBER NOT NULL,
	LENGUAJE VARCHAR(255),
	COSTO_PRODUCCION NUMBER NOT NULL,
	PUBLICO VARCHAR(255),
	DESCRIPCION VARCHAR(255),
	TRADUCCION NUMBER NOT NULL,
		
	CONSTRAINT ESPECTACULO_PK PRIMARY KEY(ID),
	CONSTRAINT FK_COMPANIA FOREIGN KEY (ID_COMPANIA) REFERENCE COMPANIA (ID)
);


COMMIT;